import React, { useState } from "react";
import './Login.scss'
import {Link} from 'react-router-dom';
import huellita from '../../assets/images/huellita.png'

function Login(){

    const[emailval, setemailval]= useState("");
    const[passval, setpassval]=useState("");
    const[passError, setPassError]=useState(false);

    function handleChange(name, value){
        if (name === 'usuario'){
            
            setemailval(value)
                
        }else{
            if(value.length < 8 ){
                setPassError(true);
            }else{
                setpassval(value)
                setPassError(false);
            }
        }
    }

    const handleSubmit=(event)=>{
        event.preventDefault();
        let account = {emailval, passval}
        if(account){
            console.log('account: ', account)
        }
    }

    return(
        <div className="main-login">
                <div className="login-container">
                      <div className = "left-side">
                        <div className="img-class">
                            <img src={huellita} id="img-id" alt="" srcset=""/>
                        </div>

                        <div className="welcomeNote">
                                <h3>¡Bienvenido a Huellitas de Esperanza!</h3>
                        </div>
                            <form onSubmit={handleSubmit}>
                            <label for="email1">Correo eléctronico</label>    
                                <input className="input" placeholder="Ingresa tu correo" type="email" value={emailval}
                                onChange={(e)=>{handleChange(e.target.name, e.target.value)}} id="email1"/>
                            <label for="pwd1">Contraseña</label>    
                                <input className="input" placeholder="Ingresa tu contraseña" type="password" 
                                value={passval} onChange={(e)=>{handleChange(e.target.name, e.target.value)}} id="pwd1"/>
                                <button type= "submit" id="sub_butt">Iniciar sesión</button>
                            </form>

                            <div className="footer">
                                <h4>No eres administrador,  <Link className='link' to="/Registro">envía una solicitud</Link></h4>
                            </div>
                        </div>
                </div>
            
        </div>
    )
}

export default Login;
